/* -------------------------------- Documents ------------------------------- */

declare module "*.html" {
  const document: any;
  export default document;
}

declare module "*.json" {
  const document: any;
  export default document;
}
/* --------------------------------- Images --------------------------------- */

declare module "*.gif" {
  const image: any;
  export default image;
}

declare module "*.jpg" {
  const image: any;
  export default image;
}

declare module "*.jpeg" {
  const image: any;
  export default image;
}

declare module "*.png" {
  const image: any;
  export default image;
}

declare module "*.svg" {
  const image: any;
  export default image;
}

/* --------------------------------- Music --------------------------------- */

declare module "*.mp3" {
  const music: any;
  export default music;
}
